/**
 * @brief 目前只支持64个参数内的反射, 更多可以通过以下生成:
import sys

n = int(sys.argv[1])
for i in range(1, n+1):
    print("#define _REFLECT_PP_FOREACH_{0}(f, {1}) ".format(i, ", ".join(["_"+str(j) for j in range(1, i+1)])), end="")
    print(" ".join(["f(_{0})".format(j) for j in range(1, i+1)]))

print("#define _REFLECT_PP_FOREACH_SIZEOF_IMPL({0}, N, ...) N".format(", ".join(["_"+str(j) for j in range(1, n+1)])))
print("#define _REFLECT_PP_FOREACH_SIZEOF(...) _REFLECT_PP_FOREACH_SIZEOF_IMPL(__VA_ARGS__, {0})".format(", ".join([str(j) for j in range(n, 0, -1)])))
 */

// 实际上的JSON序列化实现
#define _REFLECT_TO_JSON(name) \
        res += #name":"; \
        res += HX::STL::utils::toString(name); \
        res += ',';

#define _REFLECT_PP_CALL_(x, y) x##y
#define _REFLECT_PP_CALL(x, y) _REFLECT_PP_CALL_(x, y)

// 展开, 难点: 需要计算 size(...) 以展开
#define _REFLECT_PP_FOREACH(f, ...) \
_REFLECT_PP_CALL(_REFLECT_PP_FOREACH_, _REFLECT_PP_FOREACH_SIZEOF(__VA_ARGS__)) \
(_REFLECT_TO_JSON, __VA_ARGS__)

// 反射: 将成员反射以序列化成JSON字符串
#define REFLECT(...) \
std::string toString() const { \
    std::string res; \
    res += '{'; \
    _REFLECT_PP_FOREACH(_REFLECT_TO_JSON, __VA_ARGS__) \
    res.back() = '}'; \
    return res; \
}

